orderedlist.listitem:

			Open the old tutorial project and replace main.c by the
			following.

				main.c using libxml2:

#include <libxml/parser.h>			
#include <stdio.h>

int main()
{				
	xmlDocPtr doc;                                                                                                                  
	doc = xmlParseFile ("testfile.xml");

	if (doc == NULL) {
		printf ("Document not parsed successfully. \n");
		return -1;
	}
	else {
		printf ("Document parsed successfully.\n");
		xmlFreeDoc(doc);
		return 0;
	}
}

